import React, { Component } from 'react';
import '../css/content.css';

// Component
import Header from './Header';
class Content extends Component {
  render() {
    return (
      <div id="content-wrapper">
        <Header />
        <div className="container-fluid">
          <div className="card shadow mb-4">
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-bordered" id="dataTable" width="100%">
                  <thead>
                    <tr>
                      <th>Pokemon ID</th>
                      <th>Nombre del Pokemon</th>
                      <th>Imagen del Pokemon</th>
                      <th>Height</th>
                      <th>Weight</th>
                      <th>Primer movimiento</th>
                      <th>Base_experience</th>
                    </tr>
                  </thead>
                    <PokemonList/>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

class PokemonList extends Component{
  constructor(props){
    super(props);
    this.state = {
      pokemon: [],
      fetched : false,
      loading : false,
    };
  }
  componentWillMount(){
    this.setState({
      loading : true
    });
    fetch('http://pokeapi.co/api/v2/type/10').then(res=>res.json())
    .then(response=>{      
      this.setState({
        pokemon : response.pokemon,
        loading : true,
        fetched : true
      });
    });
  }
  render(){
    const {fetched, loading, pokemon} = this.state;
    let content ;
    if(fetched){
      content = <tr >{pokemon.map((pokemon, index)=><Pokemon key={pokemon.name} id={index+1} pokemon={pokemon}/>)}</tr>;
    }else if(loading && !fetched){
        content = <p> Loading ...</p>;
    }
    else{
      content = <div/>;
    }
    return  <tbody>
      {content}
    </tbody>;
  }
}

class Pokemon extends Component{
  render(){
    const {pokemon,id} = this.props;
    return (
      <tr>
        <td></td>
        <td></td>
        <td></td>
        <td></td>
        <td></td>
        <td></td>
      </tr>
    )
  }
}
export default Content;
